class WebUIProxy(private val browser: JBCefBrowserBase) {
  private val logger = Logger.getInstance(WebUIProxy::class.java)

  fun postMessage(message: ExtensionMessage) {
    val code = """
      window.dispatchEvent(new CustomEvent('message', { detail: ${gson.toJson(message)} }));
    """.trimIndent()
    logger.info(code)
    // TODO: Consider a better origin that this random made-up origin.
    browser.cefBrowser.mainFrame.executeJavaScript(code, "cody://postMessage", 0)
  }
}

class WebUIChatWindowFactory : ToolWindowFactory, DumbAware {
  override fun createToolWindowContent(project: Project, toolWindow: ToolWindow) {
    val webUiProxy = createWindow(project, toolWindow)
    WebUIChatService.getInstance(project).setWebUiProxy(webUiProxy)
  }

  private fun createWindow(project: Project, toolWindow: ToolWindow): WebUIProxy {
    val browser = JBCefBrowserBuilder().apply {
      setOffScreenRendering(false)
      // TODO: Make this conditional on running in a debug configuration.
      setEnableOpenDevToolsMenuItem(true)
    }.build()

    val viewToHost = JBCefJSQuery.create(browser as JBCefBrowserBase).apply {
      addHandler { query: String ->
        println("webview -> host: $query")
        // TODO: Agent protocol needs a way to inject onDidReceiveMessage events.
        // Thru to AgentWebViewPanel.receiveMessage

        // Fake sending messages to the webview to boot it up.
        if (query.contains("initialized")) {
          ApplicationManager.getApplication().executeOnPooledThread {
            messages.forEach {
              val code = """
              (() => {
                let e = new CustomEvent('message');
                e.data = $it;
                window.dispatchEvent(e);
              })()
              """.trimIndent()
              println("sending message to webview: $code")
              browser.cefBrowser.mainFrame.executeJavaScript(code, "cody://postMessage", 0)
            }
          }
        }

        JBCefJSQuery.Response(null)
      }
    }
    val apiScript = """
      globalThis.acquireVsCodeApi = (function() {
          let acquired = false;
          let state = undefined;

          return () => {
              if (acquired && !false) {
                  throw new Error('An instance of the VS Code API has already been acquired');
              }
              acquired = true;
              return Object.freeze({
                  postMessage: function(message, transfer) {
                    console.assert(!transfer);
                    ${viewToHost.inject("JSON.stringify(message)")}
                  },
                  setState: function(newState) {
                      state = newState;
                      // TODO: Route this to wherever VSCode sinks do-update-state.
                      // doPostMessage('do-update-state', JSON.stringify(newState));
                      console.log(`do-update-state: ${'$'}{JSON.stringify(newState)}`);
                      return newState;
                  },
                  getState: function() {
                      return state;
                  }
              });
          };
      })();
      delete window.parent;
      delete window.top;
      delete window.frameElement;
    """.trimIndent()
    browser.jbCefClient.addRequestHandler(ExtensionRequestHandler(apiScript), browser.cefBrowser)

    // TODO: What is "lockable" content?
    val lockable = false
    toolWindow.contentManager.addContent(
      ContentFactory.SERVICE.getInstance().createContent(browser.component, "TODO: Content Display Name", lockable)
    )

    browser.loadURL(MAIN_RESOURCE_URL)

    return WebUIProxy(browser)
  }
}

